import React, { Component } from "react";

class App extends Component {
  countUpdate = (event) => {
    console.log(event.currentTarget.value);
    // update that count
    // 1. take a copy of state
    const planet = this.state.planets[planet]
    const updatedcount = { 
      ...this.state.planets.planet.count,
      count: event.currentTarget.value}
    console.log(updatedcount);
    // 2. update the value
    ++event.currentTarget.value;
    // 3. set the updated object to state
    this.setState({updatedcount});
  }
  constructor(props) {
    super(props);
    this.state = {
      planets: {
        moon: [{ name: "Jonaed", count: 0 }, { name: "Ashik", count: 0 }],
        sun: [{ name: "Mission", count: 0 }, { name: "Bayjid", count: 0 }]
      }
    };
  }
  counterButtons = () => {
    console.log("this is just a simple button");
  };
  render() {
    const { planets } = this.state;
    const planet = planets.map(p => (
      planets.p
    ));
    return (
      <div className="App">
        {Object.keys(planets).map(planet => (
          <div key={planet}>
          {planet}
          <ul key={planet.person}>
            {planets[planet].map(person => (
              <li key={person.name}><button onClick={this.countUpdate} value={person.count}>{person.name}:{person.count}</button></li>
            ))}
          </ul>
          </div>
        ))}
      </div>
    );
  }
}

export default App;
